%spark
//Load Java environment vars, and convert to Scala map
import scala.collection.JavaConversions._
val jenvironmentVars = System.getenv()
//Use environmentVars map to pull environment vars for use in spark
val environmentVars = mapAsScalaMap(jenvironmentVars)
for ((k,v) <- environmentVars) println(s"key: $k, value: $v")

//Bind the hostname to the angular frontend to be used in map creation script
z.angularBind("hostname", environmentVars.getOrElse("HOSTNAME", "localhost"))

%angular
<link rel="stylesheet" href="https://unpkg.com/leaflet@1.2.0/dist/leaflet.css" />
<h3>GeoWave Leaflet Map</h3>
<div type="hidden" id="leaflet-input" host={{hostname}} />
<div id="map" style="height: 600px; width: 100%"></div>
<script type="text/javascript" id="leaflet-script">
function getHostname() {
    var element = document.getElementById('leaflet-input');
    return element.getAttribute('host');
}

function initMap() {
    var map = L.map('map').setView([50.00, 10.00], 5);

    var host = getHostname();
    mapLink = '<a href="http://www.esri.com/">Esri</a>';
    wholink = 'i-cubed, USDA, USGS, AEX, GeoEye, Getmapping, Aerogrid, IGN, IGP, UPR-EGP, and the GIS User Community';

    var basemaps = {
        OSM: L.tileLayer('http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
            attribution: 'Map data &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors',
            maxZoom: 15,
            minZoom: 2
        }),
        Satellite:L.tileLayer(
            'http://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}', {
            attribution: '&copy; '+mapLink+', '+wholink,
            maxZoom: 18,
        })
    };

    var overlays = {
        GPX:L.tileLayer.wms('http://'+host+':8000/geoserver/geowave/wms?', {
            layers: 'geowave:gpxpoint',
            format: 'image/png',
            transparent: true
        }),

        KMeansCentroids:L.tileLayer.wms('http://'+host+':8000/geoserver/geowave/wms?', {
            layers: 'geowave:mycentroids',
            format: 'image/png',
            transparent: true
        }),

        KMeansHulls:L.tileLayer.wms('http://'+host+':8000/geoserver/geowave/wms?', {
            layers: 'geowave:myhulls',
            format: 'image/png',
            transparent: true
        })
    };

    L.control.layers(basemaps, overlays).addTo(map);

    basemaps.OSM.addTo(map);
}

angular.element(document).ready(function () {
if (window.L) {
    initMap();
} else {
    console.log('Loading Leaflet library');
    var sc = document.createElement('script');
    sc.type = 'text/javascript';
    sc.src = 'https://unpkg.com/leaflet@1.2.0/dist/leaflet.js';
    sc.onload = initMap;
    sc.onerror = function(err) { alert(err); }
    document.getElementsByTagName('head')[0].appendChild(sc);
}
});
</script>

