def create_dataframe(adapter_name : String) : DataFrame = {
    //Create the dataframe and get a rdd for the output of kmeans
    var sf_df = new SimpleFeatureDataFrame(spark)
    val adapter_id = new ByteArrayId(adapter_name)

    var queryOptions = null : Option[QueryOptions]
    val adapterIt = output_store_plugin.createAdapterStore().getAdapters()
    while (adapterIt.hasNext()) {
        val adapter = adapterIt.next()
        if (adapter.getAdapterId().equals(adapter_id)) {
            val adapterForQuery = adapter
            queryOptions = Some(new QueryOptions(adapterForQuery))
        }
    }
    val loadOpts = new RDDOptions()
    loadOpts.setQueryOptions(queryOptions.getOrElse(null))
    val output_rdd = GeoWaveRDDLoader.loadRDD(sc, output_store_plugin, loadOpts))
    sf_df.init(output_store_plugin, adapter_id)

    return sf_df.getDataFrame(output_rdd)
}

var df = create_dataframe("mycentroids")
df.show()

// Convert geom string to lat/long
case class KMeansRow(lat: Double, lon: Double, ClusterIndex : Int)
val kmeansData = df.map(row => {
    val geom_index = row.fieldIndex("geom")
    val geom = row.getString(geom_index)
    val cluster_index = row.getInt(row.fieldIndex("ClusterIndex"))
    val lat_start = geom.lastIndexOf(" ") + 1
    val lat_end = geom.lastIndexOf(")")
    val lat = geom.substring(lat_start, lat_end)
    val lonStart = geom.indexOf("(") + 1
    val lonStop = geom.indexOf(" ", lonStart)
    val lon = geom.substring(lonStart, lonStop)
    KMeansRow(lat=lat.toDouble, lon=lon.toDouble, ClusterIndex=cluster_index)
    })
// send the results to the front end (Leaflet map)
z.angularBind("pins", kmeansData.collect())
// register a view for SQL queries
kmeansData.createOrReplaceTempView("kmeans")

