<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.5/leaflet.css" />
<h3>Client-side Integration</h3>
<div id="map2" style="height: 600px; width: 100%"></div>

<script type="text/javascript">
function initMap2() {
    var map2 = L.map('map2').setView([52.5, 13.4], 11);

    L.tileLayer('http://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Map data &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors',
        maxZoom: 15,
        minZoom: 2
    }).addTo(map2);

    var geoMarkers = L.layerGroup().addTo(map2);
    var markerIcon = L.icon({
        iconUrl: 'https://openstationmap.org/0.2.0/client/leaflet/images/marker-icon.png',
        iconSize: [24, 32],
    });

    var el = angular.element($('#map2').parent('.ng-scope'));
    angular.element(el).ready(function() {
        window.pinWatcher = el.scope().compiledScope.$watch('pins', function(pinList, oldValue) {
            geoMarkers.clearLayers();
            angular.forEach(pinList, function(pin) {
                var marker = L.marker([ pin.lat, pin.lon ], {icon: markerIcon})
                  .bindPopup(pin.data)
                  .addTo(geoMarkers);
            });
        })
    });}

if (window.pinWatcher) {
    // clear existing watcher otherwise we'll have duplicates
    window.pinWatcher();
}

// ensure we only load the script once, seems to cause issues otherwise
if (window.L) {
    initMap2();
} else {
    console.log('Loading Leaflet library');
    var sc = document.createElement('script');
    sc.type = 'text/javascript';
    sc.src = 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/0.7.5/leaflet.js';
    sc.onload = initMap2;
    sc.onerror = function(err) { alert(err); }
    document.getElementsByTagName('head')[0].appendChild(sc);
}
</script>
